import React, { useState, useEffect } from "react";
import { Layout } from "../components/layout/Layout";
import { MetricsGrid } from "../components/dashboard/MetricsGrid";
import { ConversionFunnel } from "../components/dashboard/ConversionFunnel";
import { Leaderboard } from "../components/dashboard/Leaderboard";
import { RecentActivity } from "../components/dashboard/RecentActivity";
import { QuickActions } from "../components/dashboard/QuickActions";
import { ChatWidget } from "../components/chat/ChatWidget";
import { LeadModal } from "../components/leads/LeadModal";
import { useAuth } from "../hooks/useAuth";
import { useLeads } from "../hooks/useFirestore";
import { getUsersByOrganization } from "../lib/firestore";
import { useToast } from "../hooks/use-toast";
import type { 
  DashboardMetrics, 
  ConversionFunnelData, 
  TopPerformer, 
  RecentActivityItem,
  User,
  Lead 
} from "../types";

export default function Dashboard() {
  const { currentUser } = useAuth();
  const { leads, loading: leadsLoading } = useLeads(currentUser?.organizationId || "");
  const [metrics, setMetrics] = useState<DashboardMetrics>({
    totalLeads: 0,
    conversionRate: 0,
    activeAgents: 0,
    revenue: 0
  });
  const [funnelData, setFunnelData] = useState<ConversionFunnelData>({
    leads: 0,
    contacted: 0,
    meetings: 0,
    conversions: 0
  });
  const [topPerformers, setTopPerformers] = useState<TopPerformer[]>([]);
  const [recentActivity, setRecentActivity] = useState<RecentActivityItem[]>([]);
  const [users, setUsers] = useState<User[]>([]);

  // LeadModal state
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedLead, setSelectedLead] = useState<Lead | undefined>();
  const [modalMode, setModalMode] = useState<"create" | "edit" | "view">("create");
  const { toast } = useToast();

  useEffect(() => {
    const fetchUsers = async () => {
      if (currentUser?.organizationId) {
        try {
          const orgUsers = await getUsersByOrganization(currentUser.organizationId);
          setUsers(orgUsers);
        } catch (error) {
          console.error("Error fetching users:", error);
        }
      }
    };

    fetchUsers();
  }, [currentUser?.organizationId]);

  useEffect(() => {
    if (!leadsLoading && leads.length > 0) {
      // Calculate metrics
      const totalLeads = leads.length;
      const contactedLeads = leads.filter(lead => 
        ["contacted", "interested", "meeting", "converted"].includes(lead.status)
      ).length;
      const convertedLeads = leads.filter(lead => lead.status === "converted").length;
      const conversionRate = totalLeads > 0 ? (convertedLeads / totalLeads) * 100 : 0;
      const activeAgents = users.filter(user => user.isActive).length;

      setMetrics({
        totalLeads,
        conversionRate,
        activeAgents,
        revenue: convertedLeads * 5000 // Estimated revenue per conversion
      });

      // Calculate funnel data
      const meetingLeads = leads.filter(lead => 
        ["meeting", "converted"].includes(lead.status)
      ).length;

      setFunnelData({
        leads: totalLeads,
        contacted: contactedLeads,
        meetings: meetingLeads,
        conversions: convertedLeads
      });

      // Generate sample top performers (in a real app, this would come from aggregated data)
      const performers: TopPerformer[] = users
        .filter(user => ["call-center", "field-agent", "supervisor"].includes(user.role))
        .slice(0, 3)
        .map((user, index) => ({
          id: user.id,
          name: user.name,
          role: user.role,
          conversions: Math.floor(Math.random() * 50) + 20,
          growth: Math.floor(Math.random() * 30) + 5
        }));

      setTopPerformers(performers);

      // Generate sample recent activity
      const activities: RecentActivityItem[] = leads
        .slice(0, 5)
        .map((lead, index) => ({
          id: `activity-${index}`,
          type: ["call", "meeting", "conversion"][Math.floor(Math.random() * 3)] as any,
          agentName: users[Math.floor(Math.random() * users.length)]?.name || "Unknown Agent",
          leadName: lead.name,
          action: "contacted",
          timestamp: new Date(Date.now() - Math.random() * 86400000) // Random time in last 24h
        }));

      setRecentActivity(activities);
    }
  }, [leads, leadsLoading, users]);

  const getDashboardTitle = () => {
    if (!currentUser) return "Dashboard";

    switch (currentUser.role) {
      case "admin":
        return "Admin Dashboard";
      case "supervisor":
        return "Supervisor Dashboard";
      case "call-center":
        return "Call Center Dashboard";
      case "field-agent":
        return "Field Agent Dashboard";
      default:
        return "Dashboard";
    }
  };

  const getDashboardSubtitle = () => {
    if (!currentUser) return "";

    switch (currentUser.role) {
      case "admin":
        return "Manage your sales organization";
      case "supervisor":
        return "Monitor team performance and approve leads";
      case "call-center":
        return "Manage assigned leads and schedule meetings";
      case "field-agent":
        return "View meetings and manage field visits";
      default:
        return "";
    }
  };

  // Quick Action handlers
  const handleAddLead = () => {
    setSelectedLead(undefined);
    setModalMode("create");
    setModalOpen(true);
  };

  const handleImportLeads = () => {
    // TODO: Implement lead import functionality
    toast({
      title: "Coming Soon",
      description: "Lead import functionality will be available soon.",
    });
  };

  const handleAddTeamMember = () => {
    // TODO: Implement add team member functionality
    toast({
      title: "Coming Soon",
      description: "Add team member functionality will be available soon.",
    });
  };

  const handleConfigureTargets = () => {
    // TODO: Implement configure targets functionality
    toast({
      title: "Coming Soon",
      description: "Configure targets functionality will be available soon.",
    });
  };

  if (!currentUser) return null;

  return (
    <>
      <Layout 
        title={getDashboardTitle()}
        subtitle={getDashboardSubtitle()}
        quickActionLabel="Add Lead"
      >
        <div data-testid="dashboard-content">
          <MetricsGrid metrics={metrics} />

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
            <ConversionFunnel data={funnelData} />
            <Leaderboard performers={topPerformers} />
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <RecentActivity activities={recentActivity} />
            <QuickActions 
              onAddLead={handleAddLead}
              onImportLeads={handleImportLeads}
              onAddTeamMember={handleAddTeamMember}
              onConfigureTargets={handleConfigureTargets}
            />
          </div>
        </div>
      </Layout>
      <ChatWidget />
      <LeadModal 
        isOpen={modalOpen}
        onClose={() => setModalOpen(false)}
        agents={users.filter(user => ["call-center", "field-agent", "supervisor"].includes(user.role))}
        mode={modalMode}
        lead={selectedLead}
      />
    </>
  );
}

client/src/pages/Leads.tsx.content:
import React, { useState, useEffect } from "react";
import { Layout } from "../components/layout/Layout";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { useAuth } from "../hooks/useAuth";
import { useLeads } from "../hooks/useFirestore";
import { formatDistanceToNow } from "date-fns";
import { Search, Filter, Plus, Eye, Edit, Trash2 } from "lucide-react";
import { LeadModal } from "../components/leads/LeadModal";
import { deleteLead, getUsersByOrganization } from "../lib/firestore";
import { useToast } from "../hooks/use-toast";
import type { Lead, LeadStatus, User } from "../types";

export default function Leads() {
  const { currentUser } = useAuth();
  const { leads, loading } = useLeads(currentUser?.organizationId || "");
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState<LeadStatus | "all">("all");
  const [modalOpen, setModalOpen] = useState(false);
  const [modalMode, setModalMode] = useState<"create" | "edit" | "view">("create");
  const [selectedLead, setSelectedLead] = useState<Lead | undefined>();
  const [agents, setAgents] = useState<User[]>([]);
  const [deleteLoading, setDeleteLoading] = useState<string | null>(null);

  // Fetch agents for assignment dropdown
  useEffect(() => {
    const fetchAgents = async () => {
      if (!currentUser?.organizationId) return;
      try {
        const orgUsers = await getUsersByOrganization(currentUser.organizationId);
        setAgents(orgUsers);
      } catch (error) {
        console.error("Error fetching agents:", error);
      }
    };
    fetchAgents();
  }, [currentUser?.organizationId]);

  // Modal handlers
  const handleCreateLead = () => {
    setSelectedLead(undefined);
    setModalMode("create");
    setModalOpen(true);
  };

  const handleViewLead = (lead: Lead) => {
    setSelectedLead(lead);
    setModalMode("view");
    setModalOpen(true);
  };

  const handleEditLead = (lead: Lead) => {
    setSelectedLead(lead);
    setModalMode("edit");
    setModalOpen(true);
  };

  const handleDeleteLead = async (leadId: string) => {
    setDeleteLoading(leadId);
    try {
      await deleteLead(leadId);
      toast({
        title: "Success",
        description: "Lead deleted successfully"
      });
    } catch (error: any) {
      console.error("Error deleting lead:", error);
      toast({
        title: "Error",
        description: error.message || "Failed to delete lead",
        variant: "destructive"
      });
    } finally {
      setDeleteLoading(null);
    }
  };

  const handleCloseModal = () => {
    setModalOpen(false);
    setSelectedLead(undefined);
  };

  const getStatusColor = (status: LeadStatus) => {
    switch (status) {
      case "new": return "bg-blue-100 text-blue-800";
      case "contacted": return "bg-yellow-100 text-yellow-800";
      case "interested": return "bg-green-100 text-green-800";
      case "meeting": return "bg-purple-100 text-purple-800";
      case "converted": return "bg-emerald-100 text-emerald-800";
      case "closed": return "bg-gray-100 text-gray-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const filteredLeads = leads.filter(lead => {
    const matchesSearch = lead.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         lead.company?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         lead.email?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === "all" || lead.status === statusFilter;

    return matchesSearch && matchesStatus;
  });

  if (!currentUser) return null;

  return (
    <Layout 
      title="Leads Management"
      subtitle="Track and manage your sales leads"
      quickActionLabel="Add Lead"
      onQuickAction={handleCreateLead}
    >
      <div data-testid="leads-content">
        {/* Filters */}
        <Card className="mb-6">
          <CardContent className="p-6">
            <div className="flex flex-col md:flex-row gap-4">
              <div className="flex-1">
                <div className="relative">
                  <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                  <Input
                    placeholder="Search leads by name, company, or email..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10"
                    data-testid="input-search-leads"
                  />
                </div>
              </div>
              <div className="flex gap-2">
                <Select value={statusFilter} onValueChange={(value: LeadStatus | "all") => setStatusFilter(value)}>
                  <SelectTrigger className="w-40" data-testid="select-status-filter">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="new">New</SelectItem>
                    <SelectItem value="contacted">Contacted</SelectItem>
                    <SelectItem value="interested">Interested</SelectItem>
                    <SelectItem value="meeting">Meeting</SelectItem>
                    <SelectItem value="converted">Converted</SelectItem>
                    <SelectItem value="closed">Closed</SelectItem>
                  </SelectContent>
                </Select>
                <Button variant="outline" size="sm" data-testid="button-filter">
                  <Filter className="h-4 w-4 mr-2" />
                  Filter
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Leads List */}
        <Card>
          <CardContent className="p-0">
            {loading ? (
              <div className="p-8 text-center text-muted-foreground" data-testid="leads-loading">
                Loading leads...
              </div>
            ) : filteredLeads.length === 0 ? (
              <div className="p-8 text-center" data-testid="leads-empty">
                <div className="text-muted-foreground mb-4">
                  {leads.length === 0 ? "No leads found" : "No leads match your search criteria"}
                </div>
                <Button onClick={handleCreateLead} data-testid="button-add-first-lead">
                  <Plus className="h-4 w-4 mr-2" />
                  Add Your First Lead
                </Button>
              </div>
            ) : (
              <div className="divide-y divide-border">
                {filteredLeads.map((lead, index) => (
                  <div 
                    key={lead.id} 
                    className="p-6 hover:bg-muted/50 transition-colors"
                    data-testid={`lead-item-${index}`}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-3 mb-2">
                          <h3 className="font-semibold text-foreground" data-testid={`lead-name-${index}`}>
                            {lead.name}
                          </h3>
                          <Badge 
                            className={getStatusColor(lead.status)}
                            data-testid={`lead-status-${index}`}
                          >
                            {lead.status}
                          </Badge>
                        </div>

                        <div className="space-y-1 text-sm text-muted-foreground">
                          {lead.company && (
                            <p data-testid={`lead-company-${index}`}>
                              <span className="font-medium">Company:</span> {lead.company}
                            </p>
                          )}
                          {lead.email && (
                            <p data-testid={`lead-email-${index}`}>
                              <span className="font-medium">Email:</span> {lead.email}
                            </p>
                          )}
                          {lead.phone && (
                            <p data-testid={`lead-phone-${index}`}>
                              <span className="font-medium">Phone:</span> {lead.phone}
                            </p>
                          )}
                          {lead.estimatedValue && (
                            <p data-testid={`lead-value-${index}`}>
                              <span className="font-medium">Est. Value:</span> ${lead.estimatedValue.toLocaleString()}
                            </p>
                          )}
                        </div>

                        {lead.notes && (
                          <p className="mt-2 text-sm text-foreground" data-testid={`lead-notes-${index}`}>
                            {lead.notes}
                          </p>
                        )}
                      </div>

                      <div className="flex flex-col items-end gap-2">
                        <p className="text-xs text-muted-foreground" data-testid={`lead-created-${index}`}>
                          Created {formatDistanceToNow(lead.createdAt, { addSuffix: true })}
                        </p>
                        <div className="flex gap-2">
                          <Button 
                            variant="outline" 
                            size="sm" 
                            onClick={() => handleViewLead(lead)}
                            data-testid={`button-view-lead-${index}`}
                          >
                            <Eye className="w-4 h-4 mr-1" />
                            View
                          </Button>
                          <Button 
                            variant="outline" 
                            size="sm" 
                            onClick={() => handleEditLead(lead)}
                            data-testid={`button-edit-lead-${index}`}
                          >
                            <Edit className="w-4 h-4 mr-1" />
                            Edit
                          </Button>
                          <AlertDialog>
                            <AlertDialogTrigger asChild>
                              <Button 
                                variant="outline" 
                                size="sm" 
                                disabled={deleteLoading === lead.id}
                                data-testid={`button-delete-lead-${index}`}
                              >
                                <Trash2 className="w-4 h-4 mr-1" />
                                {deleteLoading === lead.id ? "..." : "Delete"}
                              </Button>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                              <AlertDialogHeader>
                                <AlertDialogTitle>Delete Lead</AlertDialogTitle>
                                <AlertDialogDescription>
                                  Are you sure you want to delete {lead.name}? This action cannot be undone.
                                </AlertDialogDescription>
                              </AlertDialogHeader>
                              <AlertDialogFooter>
                                <AlertDialogCancel>Cancel</AlertDialogCancel>
                                <AlertDialogAction onClick={() => handleDeleteLead(lead.id)}>
                                  Delete
                                </AlertDialogAction>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Lead Modal */}
      <LeadModal
        isOpen={modalOpen}
        onClose={handleCloseModal}
        lead={selectedLead}
        agents={agents}
        mode={modalMode}
      />
    </Layout>
  );
}

