import React, { useState } from "react";
import { Layout } from "../components/layout/Layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { useAuth } from "../hooks/useAuth";
import { useToast } from "../hooks/use-toast";
import { createLead, getLeadsByOrganization } from "../lib/firestore";
import { parseExcelFile, createSampleExcelFile } from "../lib/excel-import";
import { ImportResultsModal } from "../components/settings/ImportResultsModal";
import type { ImportResult } from "../lib/excel-import";
import { 
  Settings as SettingsIcon, 
  Users, 
  Target, 
  DollarSign, 
  Bell, 
  Upload, 
  Download,
  MessageSquare,
  Shield,
  Globe,
  Database,
  Zap
} from "lucide-react";

export default function Settings() {
  const { currentUser } = useAuth();
  const { toast } = useToast();
  const [loading, setLoading] = useState(false);
  const [importResult, setImportResult] = useState<ImportResult | null>(null);
  const [showImportModal, setShowImportModal] = useState(false);
  const [importLoading, setImportLoading] = useState(false);

  // Organization Settings
  const [orgSettings, setOrgSettings] = useState({
    companyName: "Acme Sales Corp",
    timezone: "UTC-05:00",
    currency: "USD",
    workingHours: "9:00-17:00",
    defaultLeadStatus: "new" as const,
    autoAssignLeads: true,
    enableNotifications: true,
    allowSelfRegistration: false
  });

  // Target Settings
  const [targetSettings, setTargetSettings] = useState({
    monthlyLeadTarget: 100,
    monthlyConversionTarget: 25,
    monthlyRevenueTarget: 50000,
    targetResetDay: 1,
    enableTargetNotifications: true
  });

  // Commission Settings
  const [commissionSettings, setCommissionSettings] = useState({
    meetingCommission: 50,
    conversionCommission: 200,
    purchaseCommissionRate: 5, // percentage
    bonusThreshold: 10,
    bonusAmount: 500,
    autoApproveCommissions: false
  });

  // Notification Settings
  const [notificationSettings, setNotificationSettings] = useState({
    emailNotifications: true,
    smsNotifications: false,
    pushNotifications: true,
    dailyReports: true,
    weeklyReports: true,
    monthlyReports: true,
    newLeadAlerts: true,
    meetingReminders: true,
    targetAlerts: true
  });

  // Chat Settings
  const [chatSettings, setChatSettings] = useState({
    enableChat: true,
    rocketChatServer: "https://your-server.rocket.chat",
    enableFileSharing: true,
    maxFileSize: 10, // MB
    retentionPeriod: 90, // days
    enablePushNotifications: true
  });

  // System Settings
  const [systemSettings, setSystemSettings] = useState({
    enableDataExport: true,
    enableBulkImport: true,
    sessionTimeout: 8, // hours
    enableAuditLog: true,
    backupFrequency: "daily" as const,
    enableMaintenance: false
  });

  const handleSaveSettings = async (section: string) => {
    setLoading(true);
    try {
      // TODO: Implement actual save functionality
      await new Promise(resolve => setTimeout(resolve, 1000));
      toast({
        title: "Settings Saved",
        description: `${section} settings have been updated successfully.`
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to save settings. Please try again.",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const handleLeadImport = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.xlsx,.xls,.csv';
    input.onchange = async (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (file && currentUser?.organizationId) {
        setLoading(true);
        try {
          toast({
            title: "Processing File",
            description: `Parsing ${file.name} and checking for duplicates...`,
          });

          // Fetch existing leads for duplicate detection
          const existingLeads = await getLeadsByOrganization(currentUser.organizationId);
          const result = await parseExcelFile(file, currentUser.organizationId, existingLeads);
          setImportResult(result);
          setShowImportModal(true);
        } catch (error) {
          toast({
            title: "Import Error",
            description: "Failed to parse Excel file. Please check the format.",
            variant: "destructive"
          });
        } finally {
          setLoading(false);
        }
      }
    };
    input.click();
  };

  const handleDownloadTemplate = () => {
    try {
      createSampleExcelFile();
      toast({
        title: "Template Downloaded",
        description: "Sample Excel template has been downloaded to your computer.",
      });
    } catch (error) {
      toast({
        title: "Download Error",
        description: "Failed to download template. Please try again.",
        variant: "destructive"
      });
    }
  };

  const handleConfirmImport = async () => {
    if (!importResult || !currentUser?.organizationId) return;

    setImportLoading(true);
    try {
      let successCount = 0;
      let errorCount = 0;

      for (const leadData of importResult.validLeads) {
        try {
          await createLead(leadData);
          successCount++;
        } catch (error) {
          errorCount++;
          console.error('Error creating lead:', error);
        }
      }

      toast({
        title: "Import Complete",
        description: `Successfully imported ${successCount} leads. ${errorCount > 0 ? `${errorCount} failed.` : ''}`,
        variant: errorCount > 0 ? "destructive" : "default"
      });

      setShowImportModal(false);
      setImportResult(null);
    } catch (error) {
      toast({
        title: "Import Failed",
        description: "Failed to import leads. Please try again.",
        variant: "destructive"
      });
    } finally {
      setImportLoading(false);
    }
  };

  if (!currentUser || currentUser.role !== "admin") {
    return (
      <Layout title="Settings" subtitle="Access Denied">
        <Card>
          <CardContent className="p-8 text-center">
            <Shield className="h-16 w-16 mx-auto text-muted-foreground mb-4" />
            <h3 className="text-lg font-semibold mb-2">Admin Access Required</h3>
            <p className="text-muted-foreground">
