import React, { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useAuth } from "../../hooks/useAuth";
import { useToast } from "../../hooks/use-toast";
import { ChartLine, Users, Settings } from "lucide-react";
import { createDemoData, DEMO_CREDENTIALS } from "../../lib/demo-setup";

interface LoginModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export const LoginModal: React.FC<LoginModalProps> = ({ isOpen, onClose }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [demoLoading, setDemoLoading] = useState(false);
  const { signIn } = useAuth();
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      await signIn(email, password);
      onClose();
      toast({
        title: "Success",
        description: "You have been signed in successfully.",
      });
    } catch (error: any) {
      console.error("Sign in error:", error);

      let errorMessage = "Failed to sign in";
      if (error.message?.includes("permission-denied") || error.code === "permission-denied") {
        errorMessage = "Firebase security rules need to be configured. Please see setup instructions below.";
      } else if (error.message?.includes("user-not-found") || error.code === "auth/user-not-found") {
        errorMessage = "No account found with this email. Try setting up demo data first.";
      } else if (error.message?.includes("wrong-password") || error.code === "auth/wrong-password") {
        errorMessage = "Incorrect password. Try demo credentials or reset your password.";
      } else if (error.message) {
        errorMessage = error.message;
      }

      toast({
        title: "Sign In Failed",
        description: errorMessage,
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleDemoSetup = async () => {
    setDemoLoading(true);
    try {
      await createDemoData();
      toast({
        title: "Demo Data Created",
        description: "Demo organization and users have been created successfully!",
      });
    } catch (error: any) {
      console.error("Demo setup error:", error);
      let errorMessage = "Failed to create demo data";
      if (error.message?.includes("permission-denied")) {
        errorMessage = "Firebase security rules need to be configured first. Please follow the setup instructions.";
      } else if (error.message) {
        errorMessage = error.message;
      }

      toast({
        title: "Demo Setup Failed", 
        description: errorMessage,
        variant: "destructive",
      });
    } finally {
      setDemoLoading(false);
    }
  };

  const handleDemoLogin = async (role: keyof typeof DEMO_CREDENTIALS) => {
    const credentials = DEMO_CREDENTIALS[role];
    setEmail(credentials.email);
    setPassword(credentials.password);

    // Auto-submit after setting credentials
    setLoading(true);
    try {
      await signIn(credentials.email, credentials.password);
      onClose();
      toast({
        title: "Success",
        description: `Signed in as ${role} user successfully.`,
      });
    } catch (error: any) {
      toast({
        title: "Demo Login Failed",
        description: error.message || "Failed to sign in with demo credentials",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center" data-testid="login-modal">
      <Card className="w-full max-w-md mx-4">
        <CardContent className="pt-8">
          <div className="text-center mb-8">
            <div className="w-16 h-16 bg-primary rounded-xl flex items-center justify-center mx-auto mb-4">
              <ChartLine className="text-2xl text-primary-foreground" />
            </div>
            <h1 className="text-2xl font-bold text-foreground">Sales Management System</h1>
            <p className="text-muted-foreground mt-2">Sign in to your account</p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6" data-testid="login-form">
            <div>
              <Label htmlFor="email" className="block text-sm font-medium text-foreground mb-2">
                Email
              </Label>
              <Input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
                data-testid="input-email"
              />
            </div>

            <div>
              <Label htmlFor="password" className="block text-sm font-medium text-foreground mb-2">
                Password
              </Label>
              <Input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
                data-testid="input-password"
              />
            </div>

            <Button 
              type="submit" 
              className="w-full"
              disabled={loading}
              data-testid="button-signin"
            >
              {loading ? "Signing in..." : "Sign In"}
            </Button>

            <div className="text-center">
              <a href="#" className="text-sm text-primary hover:underline">
                Forgot your password?
              </a>
            </div>
          </form>

          {/* Demo Setup Section */}
          <div className="mt-6 pt-6 border-t border-border space-y-4">
            <div className="text-center">
              <Button
                onClick={handleDemoSetup}
                disabled={demoLoading}
                variant="outline"
                className="w-full"
                data-testid="button-setup-demo"
              >
                <Settings className="mr-2 w-4 h-4" />
                {demoLoading ? "Setting up..." : "Setup Demo Data"}
              </Button>
              <p className="text-xs text-muted-foreground mt-2">
                Creates demo organization and users for testing
              </p>
            </div>

            {/* Demo Login Buttons */}
            <div className="space-y-2">
              <p className="text-sm font-medium text-foreground text-center">Quick Demo Login:</p>
              <div className="grid grid-cols-2 gap-2">
                <Button
                  onClick={() => handleDemoLogin("admin")}
                  disabled={loading}
                  variant="secondary" 
                  size="sm"
                  data-testid="button-demo-admin"
                >
                  Admin
                </Button>
                <Button
                  onClick={() => handleDemoLogin("supervisor")}
                  disabled={loading}
                  variant="secondary"
                  size="sm" 
                  data-testid="button-demo-supervisor"
                >
                  Supervisor
                </Button>
                <Button
                  onClick={() => handleDemoLogin("agent")}
                  disabled={loading}
                  variant="secondary"
                  size="sm"
                  data-testid="button-demo-agent"
                >
                  Call Agent
                </Button>
                <Button
                  onClick={() => handleDemoLogin("field")}
                  disabled={loading}
                  variant="secondary"
                  size="sm"
                  data-testid="button-demo-field"
                >
                  Field Agent
                </Button>
              </div>
            </div>

            {/* Firebase Setup Instructions */}
            <div className="bg-muted p-3 rounded-lg">
              <p className="text-xs font-medium text-foreground mb-2">âš ï¸ Firebase Setup Required:</p>
              <div className="text-xs text-muted-foreground space-y-1">
                <p>1. <strong>Enable Authentication:</strong> Go to Firebase Console â†’ Authentication â†’ Sign-in method â†’ Enable Email/Password</p>
                <p>2. <strong>Set Firestore Rules:</strong> Go to Firestore â†’ Rules, replace with:</p>
                <div className="bg-background p-2 rounded text-xs font-mono mt-1">
                  <div>rules_version = '2';</div>
                  <div>service cloud.firestore {'{'}</div>
                  <div>&nbsp;&nbsp;match /databases/{'{database}'}/documents {'{'}</div>
                  <div>&nbsp;&nbsp;&nbsp;&nbsp;// Users can read/write their own doc</div>
                  <div>&nbsp;&nbsp;&nbsp;&nbsp;match /users/{'{{userId}}'} {'{'}</div>
                  <div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;allow read, write: if request.auth.uid == userId;</div>
                  <div>&nbsp;&nbsp;&nbsp;&nbsp;{'}'}</div>
                  <div>&nbsp;&nbsp;&nbsp;&nbsp;// Org-scoped access for demo</div>
                  <div>&nbsp;&nbsp;&nbsp;&nbsp;match /{'{document=**}'} {'{'}</div>
                  <div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;allow read, write: if request.auth != null;</div>
                  <div>&nbsp;&nbsp;&nbsp;&nbsp;{'}'}</div>
                  <div>&nbsp;&nbsp;{'}'}</div>
                  <div>{'}'}</div>
                </div>
                <p>3. Click "Publish" to save rules</p>
                <p>4. Click "Setup Demo Data" above to create test users</p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

client/src/components/chat/ChatWidget.ts.content:
import React, { useState, useRef, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { MessageSquare, Send } from "lucide-react";
import { useAuth } from "../../hooks/useAuth";
import { useChatMessages } from "../../hooks/useFirestore";
import { createChatMessage } from "../../lib/firestore";
import { formatDistanceToNow } from "date-fns";
import type { ChatMessage } from "../../types";

export const ChatWidget: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState("");
  const [sending, setSending] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { currentUser } = useAuth();
  const { messages, loading } = useChatMessages(currentUser?.organizationId || "");

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!message.trim() || !currentUser || sending) return;

    setSending(true);
    try {
      await createChatMessage({
        organizationId: currentUser.organizationId,
        senderId: currentUser.uid,
        message: message.trim(),
        type: "group",
        isRead: false
      });
      setMessage("");
    } catch (error) {
      console.error("Error sending message:", error);
    } finally {
      setSending(false);
    }
  };

  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map(n => n.charAt(0))
      .join("")
      .toUpperCase();
  };

  if (!currentUser) return null;

  return (
    <div className="fixed bottom-6 right-6 z-40" data-testid="chat-widget">
      <Button
        onClick={() => setIsOpen(!isOpen)}
        className="w-14 h-14 rounded-full shadow-lg hover:shadow-xl transition-shadow"
        data-testid="button-chat-toggle"
      >
        <MessageSquare className="text-xl" />
      </Button>

      {isOpen && (
        <Card className="absolute bottom-16 right-0 w-80 shadow-xl" data-testid="chat-panel">
          <div className="p-4 border-b border-border">
            <h3 className="font-semibold text-foreground">Team Chat</h3>
            <p className="text-sm text-muted-foreground">
              {messages.length > 0 ? `${messages.length} messages` : "No messages"}
            </p>
          </div>

          <div className="h-64 p-4 overflow-y-auto space-y-3" data-testid="chat-messages">
            {loading ? (
              <div className="text-center text-muted-foreground">Loading messages...</div>
            ) : messages.length === 0 ? (
              <div className="text-center text-muted-foreground">No messages yet</div>
            ) : (
              messages.slice().reverse().map((msg, index) => (
                <div key={msg.id} className="flex items-start space-x-2" data-testid={`chat-message-${index}`}>
                  <div className="w-6 h-6 bg-primary rounded-full flex items-center justify-center">
                    <span className="text-xs font-medium text-primary-foreground">
                      {getInitials(msg.senderId)}
                    </span>
                  </div>
                  <div className="flex-1">
                    <p className="text-sm text-foreground" data-testid={`chat-message-text-${index}`}>
                      {msg.message}
                    </p>
                    <p className="text-xs text-muted-foreground" data-testid={`chat-message-time-${index}`}>
                      {formatDistanceToNow(msg.createdAt, { addSuffix: true })}
                    </p>
                  </div>
                </div>
              ))
            )}
            <div ref={messagesEndRef} />
          </div>

          <div className="p-4 border-t border-border">
            <form onSubmit={handleSendMessage} className="flex space-x-2">
              <Input
                type="text"
                placeholder="Type a message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                disabled={sending}
                className="flex-1"
                data-testid="input-chat-message"
              />
              <Button 
                type="submit" 
                disabled={sending || !message.trim()}
                size="sm"
                data-testid="button-send-message"
              >
                <Send className="w-4 h-4" />
              </Button>
            </form>
          </div>
        </Card>
      )}
    </div>
  );
};

client/src/components/dashboard/Conversi.content:
import React from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import type { ConversionFunnelData } from "../../types";

interface ConversionFunnelProps {
  data: ConversionFunnelData;
}

export const ConversionFunnel: React.FC<ConversionFunnelProps> = ({ data }) => {
  const funnelSteps = [
    {
      label: "Leads Generated",
      value: data.leads,
      percentage: 100,
      color: "bg-blue-500"
    },
    {
      label: "Contacted",
      value: data.contacted,
      percentage: Math.round((data.contacted / data.leads) * 100),
      color: "bg-green-500"
    },
    {
      label: "Meetings Scheduled",
      value: data.meetings,
      percentage: Math.round((data.meetings / data.leads) * 100),
      color: "bg-amber-500"
    },
    {
      label: "Conversions",
      value: data.conversions,
      percentage: Math.round((data.conversions / data.leads) * 100),
      color: "bg-purple-500"
    }
  ];

  return (
    <Card className="border border-border" data-testid="conversion-funnel">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-lg font-semibold text-foreground">Conversion Funnel</h3>
          <Select defaultValue="30">
            <SelectTrigger className="w-32">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="30">Last 30 days</SelectItem>
              <SelectItem value="90">Last 90 days</SelectItem>
              <SelectItem value="365">This year</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div className="space-y-4">
          {funnelSteps.map((step, index) => (
            <div 
              key={index} 
              className="flex items-center justify-between p-4 bg-muted rounded-lg"
              data-testid={`funnel-step-${index}`}
            >
              <div className="flex items-center space-x-3">
                <div className={`w-4 h-4 ${step.color} rounded`}></div>
                <span className="font-medium">{step.label}</span>
              </div>
              <div className="text-right">
                <p className="font-semibold" data-testid={`funnel-value-${index}`}>
                  {step.value.toLocaleString()}
                </p>
                <p className="text-sm text-muted-foreground" data-testid={`funnel-percentage-${index}`}>
                  {step.percentage}%
                </p>
              </div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
};

client/src/components/dashboard/Leaderbo.content:
import React from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import type { TopPerformer } from "../../types";

interface LeaderboardProps {
  performers: TopPerformer[];
}

export const Leaderboard: React.FC<LeaderboardProps> = ({ performers }) => {
  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map(n => n.charAt(0))
      .join("")
      .toUpperCase();
  };

  const getRoleColor = (role: string) => {
    switch (role) {
      case "admin": return "bg-red-600";
      case "supervisor": return "bg-blue-600";
      case "call-center": return "bg-green-600";
      case "field-agent": return "bg-purple-600";
      default: return "bg-gray-600";
    }
  };

  return (
    <Card className="border border-border" data-testid="leaderboard">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-lg font-semibold text-foreground">Top Performers</h3>
          <Button variant="link" className="text-sm text-primary hover:underline p-0">
            View All
          </Button>
        </div>

        <div className="space-y-4">
          {performers.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground" data-testid="leaderboard-empty">
              No performance data available
            </div>
          ) : (
            performers.map((performer, index) => (
              <div 
                key={performer.id} 
                className="flex items-center justify-between p-4 bg-muted rounded-lg"
                data-testid={`performer-${index}`}
              >
                <div className="flex items-center space-x-3">
                  <div className={`w-8 h-8 ${getRoleColor(performer.role)} rounded-full flex items-center justify-center`}>
                    <span className="text-sm font-medium text-white">
                      {getInitials(performer.name)}
                    </span>
                  </div>
                  <div>
                    <p className="font-medium text-foreground" data-testid={`performer-name-${index}`}>
                      {performer.name}
                    </p>
                    <p className="text-sm text-muted-foreground capitalize" data-testid={`performer-role-${index}`}>
                      {performer.role.replace("-", " ")}
                    </p>
                  </div>
                </div>
                <div className="text-right">
                  <p className="font-semibold text-foreground" data-testid={`performer-conversions-${index}`}>
                    {performer.conversions} conversions
                  </p>
                  <p className="text-sm text-green-600" data-testid={`performer-growth-${index}`}>
                    {performer.growth > 0 ? '+' : ''}{performer.growth}% this month
                  </p>
                </div>
              </div>
            ))
          )}
        </div>
      </CardContent>
    </Card>
  );
};

client/src/components/dashboard/MetricsG.content:
import React from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Users, TrendingUp, UserCheck, DollarSign } from "lucide-react";
import type { DashboardMetrics } from "../../types";

interface MetricsGridProps {
  metrics: DashboardMetrics;
}

export const MetricsGrid: React.FC<MetricsGridProps> = ({ metrics }) => {
  const metricCards = [
    {
      title: "Total Leads",
      value: metrics.totalLeads.toLocaleString(),
      icon: Users,
      iconColor: "text-blue-600",
      iconBg: "bg-blue-100",
      change: "+12.5%",
      changeLabel: "vs last month"
    },
    {
      title: "Conversion Rate",
      value: `${metrics.conversionRate.toFixed(1)}%`,
      icon: TrendingUp,
      iconColor: "text-green-600",
      iconBg: "bg-green-100",
      change: "+3.2%",
      changeLabel: "vs last month"
    },
    {
      title: "Active Agents",
      value: metrics.activeAgents.toString(),
      icon: UserCheck,
      iconColor: "text-purple-600",
      iconBg: "bg-purple-100",
      change: "+5",
      changeLabel: "new this week"
    },
    {
      title: "Revenue",
      value: `$${(metrics.revenue / 1000).toFixed(0)}K`,
      icon: DollarSign,
      iconColor: "text-amber-600",
      iconBg: "bg-amber-100",
      change: "+18.7%",
      changeLabel: "vs last month"
    }
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8" data-testid="metrics-grid">
      {metricCards.map((metric, index) => {
        const Icon = metric.icon;

        return (
          <Card key={index} className="border border-border" data-testid={`metric-card-${index}`}>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">
                    {metric.title}
                  </p>
                  <p className="text-3xl font-bold text-foreground" data-testid={`metric-value-${index}`}>
                    {metric.value}
                  </p>
                </div>
                <div className={`w-12 h-12 ${metric.iconBg} rounded-lg flex items-center justify-center`}>
                  <Icon className={`text-xl ${metric.iconColor}`} />
                </div>
              </div>
              <div className="flex items-center mt-4 text-sm">
                <span className="text-green-600 font-medium" data-testid={`metric-change-${index}`}>
                  {metric.change}
                </span>
                <span className="text-muted-foreground ml-2">
                  {metric.changeLabel}
                </span>
              </div>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );
};

client/src/components/dashboard/QuickAct.content:
import React from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Plus, Upload, UserPlus, Settings } from "lucide-react";
import { useAuth } from "../../hooks/useAuth";

interface QuickActionsProps {
  onAddLead?: () => void;
  onImportLeads?: () => void;
  onAddTeamMember?: () => void;
  onConfigureTargets?: () => void;
}

export const QuickActions: React.FC<QuickActionsProps> = ({
  onAddLead,
  onImportLeads,
  onAddTeamMember,
  onConfigureTargets
}) => {
  const { currentUser } = useAuth();

  if (!currentUser) return null;

  const canManageTeam = ["admin", "supervisor"].includes(currentUser.role);

  return (
    <Card className="border border-border" data-testid="quick-actions">
      <CardContent className="p-6">
        <h3 className="text-lg font-semibold text-foreground mb-6">Quick Actions</h3>

        <div className="space-y-3">
          <Button 
            onClick={onAddLead}
            className="w-full flex items-center space-x-3 p-4 justify-start"
            data-testid="button-add-lead"
          >
            <Plus className="w-4 h-4" />
            <span className="font-medium">Add New Lead</span>
          </Button>

          {canManageTeam && (
            <>
              <Button 
                onClick={onImportLeads}
                variant="secondary" 
                className="w-full flex items-center space-x-3 p-4 justify-start"
                data-testid="button-import-leads"
              >
                <Upload className="w-4 h-4" />
                <span className="font-medium">Import Leads</span>
              </Button>

              <Button 
                onClick={onAddTeamMember}
                variant="secondary" 
                className="w-full flex items-center space-x-3 p-4 justify-start"
                data-testid="button-add-team-member"
              >
                <UserPlus className="w-4 h-4" />
                <span className="font-medium">Add Team Member</span>
              </Button>

              <Button 
                onClick={onConfigureTargets}
                variant="secondary" 
                className="w-full flex items-center space-x-3 p-4 justify-start"
                data-testid="button-configure-targets"
              >
                <Settings className="w-4 h-4" />
                <span className="font-medium">Configure Targets</span>
              </Button>
            </>
          )}
        </div>

        <div className="mt-8 pt-6 border-t border-border">
          <h4 className="text-sm font-semibold text-foreground mb-4">System Status</h4>
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <span className="text-sm text-muted-foreground">Active Users</span>
              <span className="text-sm font-medium text-green-600" data-testid="status-active-users">
                Online
              </span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm text-muted-foreground">Data Sync</span>
              <span className="text-sm font-medium text-green-600" data-testid="status-data-sync">
                Up to date
              </span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm text-muted-foreground">Server Status</span>
              <span className="text-sm font-medium text-green-600" data-testid="status-server">
                Healthy
              </span>
            </div>
